import "./App.css";
import { useState } from "react";

function App() {
  const [grid, setGrid] = useState(() => {
    const temp = [];
    for (let i = 0; i < 10; i++) temp.push(Array.from(Array(10), () => 0));
    return temp;
  });
  function placeShips() {
    let gridCopy = deepListCopy(grid);
    // gridCopy[][]
  }
  function deepListCopy(l) {
    return JSON.parse(JSON.stringify(l));
  }

  function gridOnClickHandler(ri, ci) {
    let gridCopy = deepListCopy(grid);
    gridCopy[ri][ci] = Number(!grid[ri][ci]);
    setGrid(gridCopy);
    console.log(i, j);
  }

  return (
    <div
      className="App"
      style={{ display: "grid", gridTemplateColumns: "repeat(10,20px)" }}
    >
      {grid.map((row, i) =>
        row.map((col, j) => (
          <div
            className="spot"
            key={`${i}-${j}`}
            onClick={() => gridOnClickHandler(i, j)}
            style={{
              backgroundColor: grid[i][j] ? "pink" : undefined,
              height: "18px",
              width: "18px",
              border: "solid 1px black",
            }}
          ></div>
        ))
      )}
    </div>
  );
}

export default App;
