import "./App.css";
import { useEffect, useState } from "react";
import Grid from './components/Grid'

function App() {




  return (
    <div
      className="App"
      style={{ display: "grid", gridTemplateColumns: "repeat(10,20px)" }}
    >
      {grid.map((row, i) =>
        row.map((col, j) => (
          <div
            className="spot"
            key={`${i}-${j}`}
            onClick={() => gridOnClickHandler(i, j)}
            style={{
              backgroundColor: handleColor(grid[i][j]),
              height: "18px",
              width: "18px",
              border: "solid 1px black",
            }}
          ></div>
        ))
      )}
    </div>
  );
}

export default App;
