import React from "react";

function App() {
  console.log("render");
  let ship4Ref = React.useRef();

  const [ships, setShips] = React.useState({
    // orientation 0 is for horizontal, 1 is for vertical
    ship4: {
      orientation: "horizontal",
      size: 4,
      reference: ship4Ref,
      position: { x: 10, y: 210 },
      // coords of ship pieces
      map: [],
      isPlaced: false,
    },
  });

  let currentShipRef = React.useRef();
  let diffXRef = React.useRef();
  let diffYRef = React.useRef();

  let grid = [];
  for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

  function deepListCopy(l) {
    return JSON.parse(JSON.stringify(l));
  }

  function handleDrop(e, gridCellRow, gridCellColumn) {
    e.preventDefault();
    //string eg. "ship4"
    let ship = currentShipRef.current;
    let shipSize = ships[ship].size;
    let shipOrientation = ships[ship].orientation;
    let shipsCopy = Object.assign({}, ships);
    shipsCopy[ship].position.x =
      gridCellColumn * 20 + 8 - diffXRef.current * 20;
    shipsCopy[ship].position.y = gridCellRow * 20 + 8 - diffYRef.current * 20;
    let gridCopy = deepListCopy(grid);
    let indexes = [];
    let col = parseInt(shipsCopy[ship].position.x / 20, 10);
    let row = parseInt(shipsCopy[ship].position.y / 20, 10);
    for (let i = 0; i < shipSize; i++) {
      switch (ships[ship].orientation) {
        case "vertical":
          indexes.push([row + i, col]);
          break;
        case "horizontal":
          indexes.push([row, col + i]);
          break;
        default:
          console.warn("ship have wrong orientation");
      }
    }
    shipsCopy[ship].map = indexes;
    shipsCopy[ship].isPlaced = true;
    setShips(shipsCopy);
    console.dir(ships);
  }

  function handleMouseClick(e, ship) {
    let debug = true;
    // 2 is for rmb
    // if (e.button !== 2) return;
    // copy ships cos useState can't be modyfied
    let shipsCopy = Object.assign({}, ships);
    // change orientation in copy
    if (ships[ship].isPlaced) {
      var row = ships[ship].map[0][0];
      var col = ships[ship].map[0][1];
      var newMap = [];
      shipsCopy[ship].map = newMap;
    }
    switch (shipsCopy[ship].orientation) {
      case "vertical":
        if (ships[ship].isPlaced) {
          for (let i = 0; i < ships[ship].size; i++) {
            newMap.push([row + i, col]);
          }
        }
        shipsCopy[ship].orientation = "horizontal";
        break;
      case "horizontal":
        if (ships[ship].isPlaced) {
          for (let i = 0; i < ships[ship].size; i++) {
            newMap.push([row, col + i]);
          }
        }
        shipsCopy[ship].orientation = "vertical";
        break;
      default:
        console.warn("wrong ship orientation in rmb");
    }
    setShips(shipsCopy);
    if (debug) {
      console.log("handleRightMouse");
      console.dir(ships);
    }
  }

  function readyCheck() {
    for (const ship in ships) {
      if (!ships.ship.isPlaced) return false;
      console.log("field is ready for game!");
    }
  }
  return (
    <div
      className="App"
      style={{
        backgroundColor: "blanchedalmond",
        width: "500px",
        height: "500px",
      }}
    >
      <div
        className="GridContainer"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(10,20px)",
          backgroundColor: "lightblue",
        }}
      >
        {grid.map((row, i) =>
          row.map((col, j) => (
            <div
              className="spot"
              key={`${i}-${j}`}
              onDragStart={(e) => e.preventDefault()}
              onDragOver={(e) => e.preventDefault()}
              onDrop={(e) => handleDrop(e, i, j)}
              style={{
                backgroundColor: "white",
                height: "18px",
                width: "18px",
                border: "solid 1px black",
              }}
            />
          ))
        )}
      </div>
      <div
        ref={ship4Ref}
        className="ship4"
        draggable
        onDragEnd={(e) => {
          ship4Ref.current.style.display = "inline";
        }}
        onDragStart={(e) => {
          setTimeout(() => (ship4Ref.current.style.display = "none"), 0);
          currentShipRef.current = "ship4";
          let grabX = e.clientX;
          let grabY = e.clientY;
          let diffX = grabX - ships.ship4.position.x;
          let diffY = grabY - ships.ship4.position.y;
          diffXRef.current = parseInt(diffX / 20);
          diffYRef.current = parseInt(diffY / 20);
        }}
        onClick={(e) => setTimeout(handleMouseClick, 0, e, "ship4")}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships.ship4.position.x,
          top: ships.ship4.position.y,
          width: () => {
            switch (ships.ship4.orientation) {
              case "horizontal":
                return `${20 * 4 - 4}px`;
                break;
              case "vertical":
                return "16px";
                break;
              default:
                return "1px";
            }
          },
          height: ships.ship4.orientation ? "16px" : `${20 * 4 - 4}px`,
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
      <button style={{ position: "absolute", right: 0 }} onClick={readyCheck}>
        Ready
      </button>
    </div>
  );
}

export default App;

