import React from "react";
const grid = [];

for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

export default function EnemyGrid(gridSize) {
    function generateGridDivs() {
        const gridDivs = [];
        grid.forEach((row, i) => {
          row.forEach((col, j) => {
            gridDivs.push(
              <div
                className="spot"
                style={{
                  backgroundColor: "white",
                  height: `${gridSize - 2}px`,
                  width: `${gridSize - 2}px`,
                  border: "solid 1px black",
                }}
                key={`${i}-${j}`}
                onDragStart={(e) => e.preventDefault()}
                onDragOver={(e) => {
                  e.preventDefault();
                  return false;
                }}
                onDrop={(e) => handleDrop(e, i, j)}
              ></div>
            );
          });
        });
        return gridDivs;
      }

  return <div className="EnemyGrid">{generateGridDivs()}</div>;
}
