import React from "react";


const grid = [];

for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

export default function EnemyGrid({ gridSize, socket, roomID }) {
React.useEffect(()=>{
  socket.on("hit_result", (data) => {
    console.log(data);
  })
},[socket])

  function generateGridDivsEnemy() {
    const gridDivs = [];
    grid.forEach((row, i) => {
      row.forEach((col, j) => {
        gridDivs.push(
          <div
            className="spot"
            style={{
              backgroundColor: "white",
              height: `${gridSize - 2}px`,
              width: `${gridSize - 2}px`,
              border: "solid 1px black",
            }}
            onDragStart={(e) => {
              e.preventDefault();
              e.stopPropagation();
              return false;
            }}
            onClick={async () => {
              const data = {
                roomID,
                row: i,
                col: j,
              };
              await socket.emit("hit", data);
            }}
            key={`${i}-${j}`}
          ></div>
        );
      });
    });
    return gridDivs;
  }

  return (
    <div
      className="EnemyGrid"
      style={{
        display: "grid",
        width: `${gridSize * 10}px`,
        gridTemplateColumns: `repeat(10,${gridSize}px)`,
        gridTemplateRows: `repeat(10,${gridSize}px)`,
      }}
    >
      {generateGridDivs()}
    </div>
  );
}
