import React from "react";
import { ships } from "../helper";

export default function Ships(gridSize) {
  function readyCheck() {
    for (const ship in ships) {
      if (!ships[ship].isPlaced) return console.log("Not all ships placed");
    }
    console.log("field is ready for game!");
  }
  const currentShipRef = React.useRef();
  const grabXRef = React.useRef();
  const grabYRef = React.useRef();

  let shipDivs = [];

  const [ships, setShips] = React.useState({
    ship4: {
      orientation: "horizontal",
      size: 4,
      reference: React.useRef(),
      position: { x: 10, y: 210 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship3_1: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 100, y: 210 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship3_2: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 100, y: 280 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_1: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 170, y: 210 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_2: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 170, y: 260 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_3: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 170, y: 310 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_1: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 220, y: 210 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_2: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 220, y: 240 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_3: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 220, y: 270 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_4: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 220, y: 300 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
  });

  for (const ship in ships) {
    shipDivs.push(
      <div
        ref={ships[ship].reference}
        key={ship}
        className={ship}
        draggable
        onDragEnd={(e) =>
          (ships[ship].reference.current.style.display = "inline")
        }
        onDragStart={(e) => {
          setTimeout(
            () => (ships[ship].reference.current.style.display = "none")
          );
          currentShipRef.current = ship;
          const diffX = e.clientX - ships[ship].position.x;
          const diffY = e.clientY - ships[ship].position.y;
          grabXRef.current = parseInt(diffX / gridSize);
          grabYRef.current = parseInt(diffY / gridSize);
        }}
        onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, ship)}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships[ship].position.x,
          top: ships[ship].position.y,
          width: (() => {
            switch (ships[ship].orientation) {
              case "horizontal":
                return `${gridSize * ships[ship].size - 4}px`;
              case "vertical":
                return "16px";
              default:
                return "100px";
            }
          })(),
          height: (() => {
            switch (ships[ship].orientation) {
              case "horizontal":
                return "16px";
              case "vertical":
                return `${gridSize * ships[ship].size - 4}px`;
              default:
                return "100px";
            }
          })(),
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
    );
  }

  return <div>
  {shipDivs}
  </div>;
}
