import { useEffect, useState } from "react";

function Grid() {
  const [grid, setGrid] = useState(() => {
    const temp = [];
    for (let i = 0; i < 10; i++) temp.push(Array.from(Array(10), () => "e"));
    return temp;
  });

  function placeShips() {
    let gridCopy = deepListCopy(grid);
    gridCopy[5][1] = "s";
    gridCopy[6][1] = "s";
    gridCopy[7][1] = "s";
    gridCopy[8][1] = "s";
    gridCopy[8][3] = "s";
    gridCopy[8][4] = "s";
    gridCopy[8][5] = "s";
    gridCopy[6][3] = "s";
    gridCopy[6][4] = "s";
    gridCopy[6][5] = "s";
    gridCopy[7][7] = "s";
    gridCopy[6][7] = "s";
    gridCopy[4][7] = "s";
    gridCopy[4][6] = "s";
    gridCopy[2][2] = "s";
    gridCopy[2][1] = "s";
    gridCopy[1][5] = "s";
    gridCopy[1][8] = "s";
    gridCopy[4][3] = "s";
    gridCopy[9][9] = "s";
    setGrid(gridCopy);
  }

  useEffect(() => {
    placeShips();
  }, []);
  function deepListCopy(l) {
    return JSON.parse(JSON.stringify(l));
  }

  function gridOnClickHandler(ri, ci) {
    let gridCopy = deepListCopy(grid);
    if (grid[ri][ci] === "s") {
      gridCopy[ri][ci] = "h";
      setGrid(gridCopy);
    } else if (grid[ri][ci] === "e") {
      gridCopy[ri][ci] = "m";
      setGrid(gridCopy);
    }
  }

  function handleColor(spot) {
    if (spot === "h") return "pink";
    if (spot === "m") return "gray";
  }

  return (
    <div
      className="App"
      style={{ display: "grid", gridTemplateColumns: "repeat(10,20px)" }}
    >
      {grid.map((row, i) =>
        row.map((col, j) => (
          <div
            className="spot"
            key={`${i}-${j}`}
            onClick={() => gridOnClickHandler(i, j)}
            style={{
              backgroundColor: handleColor(grid[i][j]),
              height: "18px",
              width: "18px",
              border: "solid 1px black",
            }}
          ></div>
        ))
      )}
    </div>
  );
}

export default Grid
