import "./App.css";
import React from "react";

function App() {
  // orientation 0 is for horizontal, 1 is for vertical
  const [ships, setShips] = React.useState({
    ship4: { orientation: 0, len: 4 },
    ship3_1: { orientation: 0, len: 3 },
  });
  let currentShipRef = React.useRef();
  const [grid, setGrid] = React.useState(() => {
    const temp = [];
    for (let i = 0; i < 10; i++) temp.push(Array.from(Array(10), () => "e"));
    return temp;
  });

  function handleDrop(e, i, j) {
    e.preventDefault();
    let ship = currentShipRef.current;
    let shipLen = ships[ship].len;
    let shipOrientation = ships[ship].orientation;

  }

  function handleRightMouseClick(e, ship) {
    // copy ships cos useState can't be modyfied
    let shipsCopy = Object.assign({}, ships);
    // change orientation in copy
    shipsCopy[ship].orientation = Number(!ships[ship].orientation);
    // change orientation in main/rerender
    setShips(shipsCopy);
  }

  return (
    <>
      <div
        className="App"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(10,20px)",
          backgroundColor: "lightblue",
        }}
      >
        {grid.map((row, i) =>
          row.map((col, j) => (
            <div
              className="spot"
              key={`${i}-${j}`}
              onDragOver={(e) => {
                e.preventDefault();
                //e.target.style.backgroundColor = "purple";
                console.log("over in grid");
              }}
              onDrop={(e) => handleDrop(e, i, j)}
              style={{
                backgroundColor: "white",
                height: "18px",
                width: "18px",
                border: "solid 1px black",
              }}
            />
          ))
        )}
      </div>
        <div
          className="ship4"
          draggable
          onDragStart={(e) => {
            currentShipRef.current = "ship4";
            console.log("dragstart ship4");
          }}
          onMouseUp={(e) => setTimeout(handleRightMouseClick, 0, e, "ship4")}
          onContextMenu={(e) => e.preventDefault()}
          style={{
            backgroundColor: "pink",
            left: "600px",
            width: ships.ship4.orientation ? `${20 * 4 - 4}px` : "16px",
            height: ships.ship4.orientation ? "16px" : `${20 * 4 - 4}px`,
            border: "solid 2px blue",
            cursor: "grab",
          }}
        ></div>
        <div
          className="ship3_1"
          draggable
          onDragStart={(e) => {
            currentShipRef.current = "ship3_1";
            console.log("dragstart ship3_1");
          }}
          onMouseUp={(e) => setTimeout(handleRightMouseClick, 0, e, "ship3_1")}
          onContextMenu={(e) => e.preventDefault()}
          style={{
            backgroundColor: "pink",
            gridRow: 2,
            width: ships.ship3_1.orientation ? `${20 * 3 - 4}px` : "16px",
            height: ships.ship3_1.orientation ? "16px" : `${20 * 3 - 4}px`,
            border: "solid 2px blue",
            cursor: "grab",
          }}
        ></div>
    </>
  );
}

export default App;
