import React from "react";
import AllyGrid from "./AllyGrid";
import EnemyGrid from "./EnemyGrid";

const gridSize = 30;

export default function SeaWars({ socket, roomID }) {
  const currentShipRef = React.useRef();
  const grabXRef = React.useRef();
  const grabYRef = React.useRef();

  const [ships, setShips] = React.useState({
    ship4: {
      orientation: "horizontal",
      size: 4,
      reference: React.useRef(),
      position: { x: 10, y: 320 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship3_1: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 140, y: 320 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship3_2: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 140, y: 420 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_1: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 240, y: 320 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_2: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 240, y: 390 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship2_3: {
      orientation: "horizontal",
      size: 2,
      reference: React.useRef(),
      position: { x: 240, y: 460 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_1: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 10, y: 450 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_2: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 50, y: 450 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_3: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 90, y: 450 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
    ship1_4: {
      orientation: "horizontal",
      size: 1,
      reference: React.useRef(),
      position: { x: 10, y: 490 },
      neighbors: [],
      map: [],
      isPlaced: false,
    },
  });

  function generateShipDivsAlly() {
    const shipDivs = [];
    for (const ship in ships) {
      shipDivs.push(
        <div
          ref={ships[ship].reference}
          key={ship}
          draggable
          onDragEnd={(e) =>
            (ships[ship].reference.current.style.display = "inline")
          }
          onDragStart={(e) => {
            setTimeout(
              () => (ships[ship].reference.current.style.display = "none")
            );
            currentShipRef.current = ship;
            const diffX = e.clientX - ships[ship].position.x;
            const diffY = e.clientY - ships[ship].position.y;
            grabXRef.current = parseInt(diffX / gridSize);
            grabYRef.current = parseInt(diffY / gridSize);
          }}
          onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, ship)}
          onContextMenu={(e) => e.preventDefault()}
          style={{
            position: "absolute",
            left: ships[ship].position.x,
            top: ships[ship].position.y,
            width: (() => {
              switch (ships[ship].orientation) {
                case "horizontal":
                  return `${gridSize * ships[ship].size - 4}px`;
                case "vertical":
                  return gridSize - 4;
                default:
                  return "100px";
              }
            })(),
            height: (() => {
              switch (ships[ship].orientation) {
                case "horizontal":
                  return gridSize - 4;
                case "vertical":
                  return `${gridSize * ships[ship].size - 4}px`;
                default:
                  return "100px";
              }
            })(),
            border: "solid 2px blue",
            cursor: "grab",
          }}
        ></div>
      );
    }
    return shipDivs;
  }

  return (
    <div
      className="App"
      style={{
        display: "grid",
        gridTemplateColumns: `repeat(2,${gridSize * 10}px)`,
        gridTemplateRows: "repeat(2,auto)",
        gap: "50px",
      }}
    >
      <AllyGrid gridSize={gridSize} />
      <EnemyGrid gridSize={gridSize} socket={socket} roomID={roomID} />
    </div>
  );
}

