import "./App.css";
import React, { useState, useEffect } from "react";

function App() {
  function dragStartHandler(e, ent) {
    console.log("dragstart ");
    console.dir(ent);
  }

  const [grid, setGrid] = useState(() => {
    const temp = [];
    for (let i = 0; i < 10; i++) temp.push(Array.from(Array(10), () => "e"));
    return temp;
  });

  return (
    <>
      <div
        className="App"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(10,20px)",
          backgroundColor: "lightblue",
        }}
      >
        {grid.map((row, i) =>
          row.map((col, j) => (
            <div
              className="spot"
              key={`${i}-${j}`}
              onDragOver={(e) => {
                e.preventDefault();
                e.target.style.backgroundColor = "purple";
                console.log("over in grid");
              }}
              onDrop={(e) => {
                e.preventDefault();
                console.log(i, j);
              }}
              style={{
                backgroundColor: "white",
                height: "18px",
                width: "18px",
                border: "solid 1px black",
              }}
            />
          ))
        )}
      </div>
      <div
        className="ShipsContainer"
        style={{
          display: "grid",
          backgroundColor: "gray",
          width: "200px",
          height: "200px",
        }}
      >
        <div
          className="ship4"
          draggable
          onDragStart={(e) => dragStartHandler(e, { onDragStartt: "test" })}
          style={{
            backgroundColor: "pink",
            width: `${20 * 4 - 4}px`,
            margin: "1px 1px",
            border: "solid 2px blue",
            height: "16px",
            cursor: "grab",
          }}
        ></div>
        <div
          className="ship3-1"
          draggable
          onDragStart={(e) => dragStartHandler(e, { onDragStartt: "test" })}
          style={{
            backgroundColor: "pink",
            width: `${20 * 3 - 4}px`,
            margin: "1px 1px",
            border: "solid 2px blue",
            height: "16px",
            cursor: "grab",
          }}
        ></div>
      </div>
    </>
  );
}

export default App;
