import React from "react";
const grid = [];

for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

export default function EnemyGrid(props) {
  const gridSize = props.gridSize;

  function generateGridDivs() {
    const gridDivs = [];
    grid.forEach((row, i) => {
      row.forEach((col, j) => {
        gridDivs.push(
          <div
            className="spot"
            style={{
              backgroundColor: "white",
              height: `${gridSize - 2}px`,
              width: `${gridSize - 2}px`,
              border: "solid 1px black",
            }}
            onDragStart={(e) => {
              e.preventDefault();
              e.stopPropagation();
              return false;
            }}
            key={`${i}-${j}`}
          ></div>
        );
      });
    });
    return gridDivs;
  }

  return (
    <div
      className="EnemyGrid"
      style={{
        display: "grid",
        width: `${gridSize * 10}px`,
        gridTemplateColumns: `repeat(10,${gridSize}px)`,
        gridTemplateRows: `repeat(10,${gridSize}px)`,
      }}
    >
      {generateGridDivs()}
    </div>
  );
}
