import React from "react";
import AllyGrid from "./AllyGrid";
import EnemyGrid from "./EnemyGrid";

const gridSize = 30;

function SeaWars({socket, username, room}) {
  return (
    <div
      className="App"
      style={{
        display: "grid",
        gridTemplateColumns: `repeat(2,${gridSize * 10}px)`,
        gridTemplateRows: "repeat(2,auto)",
        gap: "50px",
      }}
    >
      <AllyGrid gridSize={gridSize} />
      <EnemyGrid gridSize={gridSize} />
    </div>
  );
}

export default SeaWars;

