import React from "react";
import io from "socket.io-client";

const socket = io.connect("http://localhost:3001");

const grid = [];

for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

export default function EnemyGrid({ gridSize, socket, roomID }) {
  function generateGridDivs() {
    const gridDivs = [];
    grid.forEach((row, i) => {
      row.forEach((col, j) => {
        gridDivs.push(
          <div
            className="spot"
            style={{
              backgroundColor: "white",
              height: `${gridSize - 2}px`,
              width: `${gridSize - 2}px`,
              border: "solid 1px black",
            }}
            onDragStart={(e) => {
              e.preventDefault();
              e.stopPropagation();
              return false;
            }}
            onClick={() => {
              const data = {
                roomID,
                row: i,
                col: j,
              };
              socket.emit()
            }}
            key={`${i}-${j}`}
          ></div>
        );
      });
    });
    return gridDivs;
  }

  return (
    <div
      className="EnemyGrid"
      style={{
        display: "grid",
        width: `${gridSize * 10}px`,
        gridTemplateColumns: `repeat(10,${gridSize}px)`,
        gridTemplateRows: `repeat(10,${gridSize}px)`,
      }}
    >
      {generateGridDivs()}
    </div>
  );
}
