import React from "react";

let grid = [];
for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

let gridSize = 20;

function errorHandle(shipsObjRefCurrent) {
  shipsObjRefCurrent.style.borderColor = "red";
  setTimeout(() => (shipsObjRefCurrent.style.borderColor = "blue"), 500);
  return false;
}

function App() {
  function handleDrop(e, dropRow, dropCol) {
    e.stopPropagation();
    e.preventDefault();

    const currentShipName = currentShipRef.current;
    const shipObj = ships[currentShipName];
    const shipsObjRefCurrent = shipObj.reference.current;

    const grabX = grabXRef.current;
    const grabY = grabYRef.current;

    const placeX = dropCol * gridSize - grabX * gridSize + 8;
    const placeY = dropRow * gridSize - grabY * gridSize + 8;

    const placeCol = Math.floor(placeX / gridSize);
    const placeRow = Math.floor(placeY / gridSize);

    switch (shipObj.orientation) {
      case "horizontal":
        if (placeCol > 10 - shipObj.size || placeCol < 0) {
          errorHandle(shipsObjRefCurrent);
          return false;
        }
        break;
      case "vertical":
        if (placeRow > 10 - shipObj.size || placeRow < 0) {
          errorHandle(shipsObjRefCurrent);
          return false;
        }
        break;
      default:
        console.warn(
          "ship have wrong orientation in dropHandle misplace check"
        );
        return false;
    }

    let shipsCopy = Object.assign({}, ships);

    shipsCopy[currentShipName].position.x = placeX;
    shipsCopy[currentShipName].position.y = placeY;

    let newMap = [];

    for (let i = 0; i < shipObj.size; i++) {
      switch (shipObj.orientation) {
        case "vertical":
          newMap.push([placeRow + i, placeCol]);
          break;
        case "horizontal":
          newMap.push([placeRow, placeCol + i]);
          break;
        default:
          console.warn(
            "ship have wrong orientation in dripHandle, newMap loop"
          );
      }
    }

    shipsCopy[currentShipName].map = newMap;
    shipsCopy[currentShipName].isPlaced = true;
    setShips(shipsCopy);

    console.dir(ships);

    return false;
  }

  function handleMouseClick(e, shipName) {
    if (e.button !== 2) return;

    const shipObj = ships[shipName];
    const shipMap = shipObj.map;
    const shipObjRefCurrent = shipObj.reference.current;

    let shipsCopy = Object.assign({}, ships);

    if (shipObj.isPlaced) {
      var row = shipMap[0][0];
      var col = shipMap[0][1];

      shipsCopy[shipName].map = [];

      switch (shipObj.orientation) {
        case "vertical":
          if (col > 10 - shipObj.size) return errorHandle(shipObjRefCurrent);
          break;
        case "horizontal":
          if (row > 10 - shipObj.size) return errorHandle(shipObjRefCurrent);
          break;
        default:
          console.warn("wrong ship orientation in start rmb handler");
      }
    }

    switch (shipObj.orientation) {
      case "horizontal":
        if (!shipObj.isPlaced) break;

        for (let i = 0; i < shipObj.size; i++) {
          shipsCopy[shipName].map.push([row + i, col]);
        }

        shipsCopy[shipName].orientation = "vertical";
        break;

      case "vertical":
        if (!shipObj.isPlaced) break;

        for (let i = 0; i < shipObj.size; i++) {
          shipsCopy[shipName].map.push([row, col + i]);
        }

        shipsCopy[shipName].orientation = "horizontal";
        break;

      default:
        console.warn("wrong ship orientation in rmb");
    }

    setShips(shipsCopy);
  }

  function deepListCopy(l) {
    return JSON.parse(JSON.stringify(l));
  }

  function readyCheck() {
    for (const ship in ships) {
      if (!ships[ship].isPlaced) return false;
      console.log("field is ready for game!");
    }
  }

  const [ships, setShips] = React.useState({
    ship4: {
      orientation: "horizontal",
      size: 4,
      reference: React.useRef(),
      position: { x: 10, y: 210 },
      map: [],
      isPlaced: false,
    },
    ship3_1: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 10, y: 240 },
      map: [],
      isPlaced: false,
    },
  });

  const currentShipRef = React.useRef();
  const grabXRef = React.useRef();
  const grabYRef = React.useRef();

  return (
    <div
      className="App"
      style={{
        backgroundColor: "blanchedalmond",
        width: "500px",
        height: "500px",
      }}
    >
      <div
        className="GridContainer"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(10,20px)",
          backgroundColor: "lightblue",
        }}
      >
        {grid.map((row, i) =>
          row.map((col, j) => (
            <div
              className="spot"
              key={`${i}-${j}`}
              onDragStart={(e) => e.preventDefault()}
              onDragOver={(e) => {
                e.preventDefault();
                return false;
              }}
              onDrop={(e) => handleDrop(e, i, j)}
              style={{
                backgroundColor: "white",
                height: "18px",
                width: "18px",
                border: "solid 1px black",
              }}
            />
          ))
        )}
      </div>
      <div
        ref={ships.ship4.reference}
        className="ship4"
        draggable
        onDragEnd={(e) =>
          (ships.ship4.reference.current.style.display = "inline")
        }
        onDragStart={(e) => {
          setTimeout(
            () => (ships.ship4.reference.current.style.display = "none")
          );
          currentShipRef.current = "ship4";
          const diffX = e.clientX - ships.ship4.position.x;
          const diffY = e.clientY - ships.ship4.position.y;
          grabXRef.current = parseInt(diffX / gridSize);
          grabYRef.current = parseInt(diffY / gridSize);
        }}
        onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, "ship4")}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships.ship4.position.x,
          top: ships.ship4.position.y,
          width: (() => {
            switch (ships.ship4.orientation) {
              case "horizontal":
                return `${gridSize * 4 - 4}px`;
              case "vertical":
                return "16px";
              default:
                return "100px";
            }
          })(),
          height: (() => {
            switch (ships.ship4.orientation) {
              case "horizontal":
                return "16px";
              case "vertical":
                return `${gridSize * 4 - 4}px`;
              default:
                return "100px";
            }
          })(),
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
      <div
        ref={ships.ship3_1.reference}
        className="ship3_1"
        draggable
        onDragEnd={(e) =>
          (ships.ship3_1.reference.current.style.display = "inline")
        }
        onDragStart={(e) => {
          setTimeout(
            () => (ships.ship3_1.reference.current.style.display = "none")
          );
          currentShipRef.current = "ship3_1";
          const diffX = e.clientX - ships.ship3_1.position.x;
          const diffY = e.clientY - ships.ship3_1.position.y;
          grabXRef.current = parseInt(diffX / gridSize);
          grabYRef.current = parseInt(diffY / gridSize);
        }}
        onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, "ship3_1")}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships.ship3_1.position.x,
          top: ships.ship3_1.position.y,
          width: (() => {
            switch (ships.ship3_1.orientation) {
              case "horizontal":
                return `${gridSize * 3 - 4}px`;
              case "vertical":
                return "16px";
              default:
                return "100px";
            }
          })(),
          height: (() => {
            switch (ships.ship3_1.orientation) {
              case "horizontal":
                return "16px";
              case "vertical":
                return `${gridSize * 3 - 4}px`;
              default:
                return "100px";
            }
          })(),
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
      <button style={{ position: "absolute", right: 0 }} onClick={readyCheck}>
        Ready
      </button>
    </div>
  );
}

export default App;

