import React from "react";

let grid = [];
for (let i = 0; i < 10; i++) grid.push(Array.from(Array(10), () => 0));

let gridSize = gridSize;

function App() {
  function handleDrop(e, dropRow, dropCol) {
    function errorHandle() {
      shipObj.reference.current.style.borderColor = "red";
      setTimeout(() => (shipsObjRefCurrent.style.borderColor = "blue"), 500);
      isShipPlacedOutsideGrid = true;
    }
    e.stopPropagation();
    e.preventDefault();

    let isShipPlacedOutsideGrid = false;

    const [grabXRef, grabYRef] = grabStartCoordsRefs;
    console.log(grabX);
    console.log(grabY);

    const placeCol = Math.floor(
      (dropCol * gridSize - grabX * gridSize + 8) / gridSize
    );

    const placeRow = Math.floor(
      (dropRow * gridSize - grabY * gridSize + 8) / gridSize
    );

    const currentShipName = currentShipRef.current;
    const shipObj = ships[currentShipName];
    const shipsObjRefCurrent = shipObj.reference.current;

    if (shipObj.orientation === "horizontal") {
      if (dropCol > 10 - shipObj.size || dropCol < 0) {
        errorHandle();
      }
    } else if (shipObj.orientation === "vertical") {
      if (dropRow > 10 - shipObj.size || dropRow < 0) {
        errorHandle();
      }
    }
    if (!isShipPlacedOutsideGrid) {
      let shipsCopy = Object.assign({}, ships);

      shipsCopy[currentShipName].position.x =
        dropCol * gridSize - grabX * gridSize + 8;
      shipsCopy[currentShipName].position.y =
        dropRow * gridSize - grabX * gridSize + 8;

      let newMap = [];

      let startCol = parseInt(
        shipsCopy[currentShipName].position.x / gridSize,
        10
      );

      let startRow = parseInt(
        shipsCopy[currentShipName].position.y / gridSize,
        10
      );

      for (let i = 0; i < shipSize; i++) {
        switch (shipOrientation) {
          case "vertical":
            newMap.push([startRow + i, startCol]);
            break;
          case "horizontal":
            newMap.push([startRow, startCol + i]);
            break;
          default:
            console.warn("ship have wrong orientation");
        }
      }

      shipsCopy[currentShipName].map = newMap;
      shipsCopy[currentShipName].isPlaced = true;
      setShips(shipsCopy);
    }
    return false;
  }

  function handleMouseClick(e, ship) {
    if (e.button !== 2) return;
    let shipsCopy = Object.assign({}, ships);
    if (ships[ship].isPlaced) {
      var row = ships[ship].map[0][0];
      var col = ships[ship].map[0][1];
      switch (ships[ship].orientation) {
        case "vertical":
          if (col > 10 - ships[currentShipRef.current].size) {
            ships[ship].reference.current.style.borderColor = "red";
            setTimeout(
              () => (ships[ship].reference.current.style.borderColor = "blue"),
              500
            );
            return;
          }
          break;
        case "horizontal":
          if (row > 10 - ships[currentShipRef.current].size) {
            ships[ship].reference.current.style.borderColor = "red";
            setTimeout(
              () => (ships[ship].reference.current.style.borderColor = "blue"),
              500
            );
            return;
          }
          break;
        default:
          console.warn("wrong ship orientation in start rmb handler");
      }
      shipsCopy[ship].map = [];
    }
    switch (shipsCopy[ship].orientation) {
      case "horizontal":
        if (ships[ship].isPlaced) {
          for (let i = 0; i < ships[ship].size; i++) {
            shipsCopy[ship].map.push([row + i, col]);
          }
        }
        shipsCopy[ship].orientation = "vertical";
        break;
      case "vertical":
        if (ships[ship].isPlaced) {
          for (let i = 0; i < ships[ship].size; i++) {
            shipsCopy[ship].map.push([row, col + i]);
          }
        }
        shipsCopy[ship].orientation = "horizontal";
        break;
      default:
        console.warn("wrong ship orientation in rmb");
    }
    console.dir(ships);
    setShips(shipsCopy);
  }

  function deepListCopy(l) {
    return JSON.parse(JSON.stringify(l));
  }

  function readyCheck() {
    for (const ship in ships) {
      if (!ships[ship].isPlaced) return false;
      console.log("field is ready for game!");
    }
  }

  const [ships, setShips] = React.useState({
    ship4: {
      orientation: "horizontal",
      size: 4,
      reference: React.useRef(),
      position: { x: 10, y: 210 },
      map: [],
      isPlaced: false,
    },
    ship3_1: {
      orientation: "horizontal",
      size: 3,
      reference: React.useRef(),
      position: { x: 10, y: 240 },
      map: [],
      isPlaced: false,
    },
  });

  const currentShipRef = React.useRef();
  const grabStartCoordsRefs = { x: React.useRef(), y: React.useRef() };

  return (
    <div
      className="App"
      style={{
        backgroundColor: "blanchedalmond",
        width: "500px",
        height: "500px",
      }}
    >
      <div
        className="GridContainer"
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(10,20px)",
          backgroundColor: "lightblue",
        }}
      >
        {grid.map((row, i) =>
          row.map((col, j) => (
            <div
              className="spot"
              key={`${i}-${j}`}
              onDragStart={(e) => e.preventDefault()}
              onDragOver={(e) => {
                e.preventDefault();
                return false;
              }}
              onDrop={(e) => handleDrop(e, i, j)}
              style={{
                backgroundColor: "white",
                height: "18px",
                width: "18px",
                border: "solid 1px black",
              }}
            />
          ))
        )}
      </div>
      <div
        ref={ships.ship4.reference}
        className="ship4"
        draggable
        onDragEnd={(e) =>
          (ships.ship4.reference.current.style.display = "inline")
        }
        onDragStart={(e) => {
          setTimeout(
            () => (ships.ship4.reference.current.style.display = "none")
          );
          currentShipRef.current = "ship4";
          const diffX = e.clientX - ships.ship4.position.x;
          const diffY = e.clientY - ships.ship4.position.y;
          diffXRef.current = parseInt(diffX / gridSize);
          diffYRef.current = parseInt(diffY / gridSize);
        }}
        onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, "ship4")}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships.ship4.position.x,
          top: ships.ship4.position.y,
          width: (() => {
            switch (ships.ship4.orientation) {
              case "horizontal":
                return `${gridSize * 4 - 4}px`;
              case "vertical":
                return "16px";
              default:
                return "100px";
            }
          })(),
          height: (() => {
            switch (ships.ship4.orientation) {
              case "horizontal":
                return "16px";
              case "vertical":
                return `${gridSize * 4 - 4}px`;
              default:
                return "100px";
            }
          })(),
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
      <div
        ref={ships.ship3_1.reference}
        className="ship3_1"
        draggable
        onDragEnd={(e) =>
          (ships.ship3_1.reference.current.style.display = "inline")
        }
        onDragStart={(e) => {
          setTimeout(
            () => (ships.ship3_1.reference.current.style.display = "none")
          );
          currentShipRef.current = "ship3_1";
          const diffX = e.clientX - ships.ship3_1.position.x;
          const diffY = e.clientY - ships.ship3_1.position.y;
          diffXRef.current = parseInt(diffX / gridSize);
          diffYRef.current = parseInt(diffY / gridSize);
        }}
        onMouseUp={(e) => setTimeout(handleMouseClick, 0, e, "ship3_1")}
        onContextMenu={(e) => e.preventDefault()}
        style={{
          position: "absolute",
          left: ships.ship3_1.position.x,
          top: ships.ship3_1.position.y,
          width: (() => {
            switch (ships.ship3_1.orientation) {
              case "horizontal":
                return `${gridSize * 3 - 4}px`;
              case "vertical":
                return "16px";
              default:
                return "100px";
            }
          })(),
          height: (() => {
            switch (ships.ship3_1.orientation) {
              case "horizontal":
                return "16px";
              case "vertical":
                return `${gridSize * 3 - 4}px`;
              default:
                return "100px";
            }
          })(),
          border: "solid 2px blue",
          cursor: "grab",
        }}
      ></div>
      <button style={{ position: "absolute", right: 0 }} onClick={readyCheck}>
        Ready
      </button>
    </div>
  );
}

export default App;

